var jsTheme={init:function(){jsTheme.mobileNav.init(),jsTheme.forms.init()}};jsTheme.mobileNav={init:function(){jsTheme.mobileNav.enableMobileNav(),jsTheme.mobileNav.buildMobileNav()},enableMobileNav:function(){$("html").addClass("mobile-nav")},buildMobileNav:function(){var i=$(".header .inner");i.prepend('<span class="main-nav-trigger">menu</span>');var e=$(".main-nav-trigger"),n=$(".main-nav");e.on("click",function(){n.toggle(),$(this).toggleClass("trigger-active")})}},jsTheme.forms={init:function(){$(".alert-box").on("click",function(i){i.preventDefault(),$(this).closest(".alert-box").fadeOut(300)})}},$(jsTheme.init);var pixelRatio=window.devicePixelRatio?window.devicePixelRatio:1;$(window).on("load",function(){pixelRatio>1&&$("img").each(function(){$(this).attr("src",$(this).attr("src").replace(".","@2x."))})});